import { useRef, useState, useEffect } from "react";
import {
    faCheck,
    faTimes,
    faInfoCircle,
  } from "@fortawesome/free-solid-svg-icons";
  import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
export default function Login(){

    const userRef = useRef();

    const [user,setUser]= useState("")

    const [password,setPassword]=useState("")


    return(
        <section className="blur-container">
        <div  className="background">
         <div className="shape"></div>
         <div className="shape"></div>
     </div>
       
 
       <form onSubmit={handleSubmit}>
       <h1>Register</h1>
         {/* //user name */}
         <label htmlFor="username">
           User name
           
         </label>
         <input
           type="text"
           id="username"
           onChange={(e) => setUser(e.target.value)}
           ref={userRef}
           autoComplete="off"
           required
           onFocus={() => setUserFocus(true)}
           onBlur={() => setUserFocus(false)}
         ></input>
         <p className={userFocus && user && !validUser ? "hi" : "offscreen"}>
           <FontAwesomeIcon icon={faInfoCircle} />
           4 to 24 characters.
           <br />
           Must begin with a letter.
           <br />
           Letters, numbers,
           <br />
           underscores, hyphens allowed.
         </p>
 
         {/* password */}
 
         <label htmlFor="password">
           password
           {password && validPassword && <FontAwesomeIcon   className="check"     icon={faCheck} />}
           {password && !validPassword && <FontAwesomeIcon  className="not-check" icon={faTimes} />}
         </label>
         <input
           type="password"
           id="password"
           onChange={(e) => setPassword(e.target.value)}
           autoComplete="off"
           required
           onFocus={() => setPwdFocus(true)}
           onBlur={() => setPwdFocus(false)}
         ></input>
         <p
           className={
             pwdFocus && password && !validPassword ? "hi" : "offscreen"
           }
         >
           <FontAwesomeIcon icon={faInfoCircle} />
           8 to 24 characters.
           <br />
           Must include uppercase 
           <br /> and lowercase letters,
           <br />
            a number and a special
           character.
           <br />
           Allowed special characters:{" "}
           <span aria-label="exclamation mark">!</span>{" "}
           <span aria-label="at symbol">@</span>{" "}
           <span aria-label="hashtag">#</span>{" "}
           <span aria-label="dollar sign">$</span>{" "}
           <span aria-label="percent">%</span>
         </p>
         
 
         <button disabled={validMatchPwd && validPassword && validUser? false:true}> Login</button>
       </form>
     </section>
    )
}
