    } 
  ;
    return(
        <section className="blur-container">
        <div className="background">
          <div className="shape"></div>
          <div className="shape"></div>
        </div>
  
        <form onSubmit={handleSubmit}>
          <h1>VACCINE VERIFICATION</h1>
          <div style={{ background: 'white', padding: '16px' }}>
    <QRCode val/>
</div>
  
