          <input
            type="text"
            id="username"
            onChange={(e) => setUser(e.target.value)}
            ref={userRef}
            autoComplete="off"
            required
            value={user}
          ></input>
          <button> VERIFICATION </button>
  
         
          
        </form>
      </section>
    )
}
